//     top: 1vh;

// `;

const Item = styled.div`
  background-color: ${props => props.theme.toggleBackground};
  color: ${props => props.theme.textColor};
`;
  // color: ${props => props.theme.textColor2};

function App(props) { 

  const [theme, setTheme] = useState("light")

  function changeTheme() {
    if (theme === "light") {
        setTheme("dark");
    } else {
        setTheme("light");
    }
  };

  const icon = theme === "light" ? <HiMoon size={40} /> : <CgSun size={40} />;


  return (
    <div className="App">
      <ThemeProvider>
        <Page>


        {/* <Splash theme={theme} setTheme={setTheme}/> */}
        {/* <NavHeader /> */}
            <Header theme={this.theme} setTheme={this.setTheme} changeTheme={changeTheme}/>



            <LandingPage/>
            <ResumePage/>
            {/* <EducationPage theme={props.theme} /> */}
            <EducationPage />
            <ProjectsPage/>
            <WorkPage/>
            <InterestsPage/>
            <ContactPage/>
            {/* <Footer/> */}

          </Page>
      </ThemeProvider>
    </div>
  );
}

export default App;
