import React, { useState }from "react";
import Carousel from 'react-bootstrap/Carousel'
import RecentPostsList from "./RecentPostsList";

import {ImEyeBlocked, ImEye} from "react-icons/im";
import {GiTornado} from "react-icons/gi";
import { Children } from "react";

const RecentPostsCarousel = () => {
    const [index, setIndex] = useState(0);
    const [iconVisible, setIconVisible] = useState(false);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };



  const hasClassName = (inElement, inClassName) =>
  {
      let regExp = new RegExp('(?:^|\\s+)' + inClassName + '(?:\\s+|$)');
      return regExp.test(inElement.className);
  }

  const addClassName = (inElement, inClassName) =>
  {
      if (!hasClassName(inElement, inClassName))
          inElement.className = [inElement.className, inClassName].join(' ');
  }

  const removeClassName = (inElement, inClassName) =>
  {
      if (hasClassName(inElement, inClassName)) {
          let regExp = new RegExp('(?:^|\\s+)' + inClassName + '(?:\\s+|$)', 'g');
          let curClasses = inElement.className;
          inElement.className = curClasses.replace(regExp, ' ');
      }
  }

  const toggleClassName = (inElement, inClassName) =>
  {
      if (hasClassName(inElement, inClassName))
          removeClassName(inElement, inClassName);
      else
          addClassName(inElement, inClassName);
  }

  const toggleShape= ()=>
  {
    let shape = document.getElementById('shape');
    if (hasClassName(shape, 'ring')) {
      removeClassName(shape, 'ring');
      addClassName(shape, 'cube');
    } else {
      removeClassName(shape, 'cube');
      addClassName(shape, 'ring');
    }

    // Move the ring back in Z so it's not so in-your-face.
    let stage = document.getElementById('stage');
    if (hasClassName(shape, 'ring'))
      stage.style.webkitTransform = 'translateZ(-200px)';
    else
      stage.style.webkitTransform = '';
  }


  const toggleBackfaces = ()=>
    {
      let backfacesVisible = document.getElementById('backfaces').checked;
      let shape = document.getElementById('shape');
      if (backfacesVisible){
        addClassName(shape, 'backfaces')
        setIconVisible(!iconVisible);
      }
      else{
          removeClassName(shape, 'backfaces')
          setIconVisible(iconVisible);
      }
  }

  const toggleSpinningSpeed = () =>
  {
    let shape = document.getElementById('shape');
    if (hasClassName(shape, 'ring')) {
      if (!hasClassName(shape, 'verySlow')) {
          removeClassName(shape, 'quickSpin');
          addClassName(shape, 'verySlow');
      } else if(hasClassName(shape, 'verySlow')&& !hasClassName(shape, 'quickSpin')) {
          removeClassName(shape, 'verySlow');
          addClassName(shape, 'quickSpin');
      }
    } else {
      // removeClassName(shape, 'cube');
      // addClassName(shape, 'ring');
      if (!hasClassName(shape, 'verySlow')) {
        removeClassName(shape, 'quickSpin');
        addClassName(shape, 'verySlow');
    } else if(hasClassName(shape, 'verySlow')&& !hasClassName(shape, 'quickSpin')) {
        removeClassName(shape, 'verySlow');
        addClassName(shape, 'quickSpin');
    }
    }
  }


  return (
    <>
    {/* <Carousel activeIndex={index} onSelect={handleSelect}> */}
        {/* <RecentPostsList/> */}
    {/* </Carousel> */}
      <div id="recentPostsContainer">
        <section className="controls">
          <span onClick={toggleBackfaces} id="backfacesContainer">
              <input type="checkbox" id="backfaces"  checked="false"/>
              {iconVisible==true?
                <>
                  <label for="backfaces" className="visibleIcon controlsIcon">
                    <ImEyeBlocked/>
                  </label>
                </>
                : 
                <>
                  <label for="backfaces" className="controlsIcon invisibleIcon">
                    <ImEye/>
                  </label>
                </>
              }
          </span>
          <a ><i className="controlsIcon" onClick={toggleSpinningSpeed}><GiTornado/></i></a>
        </section>

        <div id="container" onClick={toggleShape}>
          <div id="stage" >
            <div id="shape" className="backfaces cube">
              <div className="plane one">1</div>
              <div className="plane two">2</div>
              <div className="plane three">3</div>
              <div className="plane four">4</div>
              <div className="plane five">5</div>
              <div className="plane six">6</div>
              <div className="plane seven">7</div>
              <div className="plane eight">8</div>
              <div className="plane nine">9</div>
              <div className="plane ten">10</div>
              <div className="plane eleven">11</div>
              <div className="plane twelve">12</div>
              </div>
            </div>
        </div>
      </div>


    </>
  );
}

export default RecentPostsCarousel;
