import React, {Component, setState} from "react";

class Project extends Component {
    state = {
        active: false,
        visited: false,
    }
    // this.setActive= this.setActive.bind(this)

    // setActive = () => {
    //     if(!this.state.active){
    //         this.setState(state => (
    //             {active: !this.state.active}
    //         )
    //     )}
    //     else {
    //         this.setState(
    //             {active: false}
    //         )}
    // };
    setActive = () => {
        {!this.state.active ?
            this.setState(state => (
                {active: !this.state.active}
            ))
        :

            this.setState(
                {active: false}
            )
        }
    };

    setVisited = () => {
        {!this.state.visited ?
            this.setState(
                {visited: true})
        :
            this.setState(
                {visited: false})
        }
    }

    render() {
        return(
            <div className="projectContainer animate">
                {this.state.active ?
                <>
                <div className="projectNameLogo inactiveProject">
                    <img className="projectLogoActive" src={this.props.logo} alt={this.props.name + "logo"} style={this.props.style}/>
                    <h3 className="projectName">{this.props.name}</h3>
                </div>
                <div className="projectDescription"><p >{this.props.description}</p></div>
                <div className="projectMore">MORE</div>
                <div className="projectGallery">Gallery
                    <div className="projectPhotos">Website Photos</div>
                    <div className="projectCode">Code Photos</div>
                </div>
                </> : 
                <>
                {this.state.visited ?
                    <>
                    <div className="visitedProject droppable">
                        <img className="projectLogoInactive" src={this.props.logo} alt={this.props.name + "logo"} style={this.props.style}/>
                    </div>
                    </> :
                    <>
                    <div className="activeProject" onClick={this.setActive}>
                        <img className="projectLogoInactive" src={this.props.logo} alt={this.props.name + "logo"} style={this.props.style}/>
                    </div>
                    </>
                }
                </>
                }
            </div>
        )}
}

export default Project;
