import React, {useState} from 'react'
// import './Carousel.scss'

const Carousel = (props) => {
    const [currentImage, setCurrentImage] = useState(0)
    const selectLeft = () => setCurrentImage((currentImage - 1 + props.images.length) % props.images.length)
    const selectRight = () => setCurrentImage((currentImage + 1) % props.images.length)

    const images = props.gallery.map(image => {
    return <img src={image.img} alt={image.descImg}/>
    })

    return (
        <div className="carousel-container">
            {props.images.length > 1 && <button className="left-button" onClick={selectLeft}>&lt;</button>}
            <div className="carousel-image-container">
                {images[currentImage]}
            </div>
            {props.images.length > 1 && <button className="right-button"  onClick={selectRight}>&gt;</button>}
            <div><p>{image[currentImage].descImg}</p></div>
        </div>
    )
}
export default Carousel
