import React from 'react';
import PropTypes from 'prop-types';

class Title extends React.Component {
    constructor(props){
        super(props);
        props={
            text1 : text1,
            text2 : text2,
            text3 : text3,
        }
        this.state = {
            x: 0,
            y: 0
        }
    }

    _onMouseMove = (e) => {
        const width = this.refs.titleContainer.clientWidth;
        const height = this.refs.titleContainer.clientHeight;
        // console.log(e.nativeEvent, width, height);
        const oX = (e.nativeEvent.offsetX/width)*100;
        const oY = (e.nativeEvent.offsetY/height)*100;
        // console.log(Math.floor(oX), Math.floor(oY));
        this.setState({
            x:oX,
            y: oY
        })
    }


    render() {
        // const {text} = this.props;
        const {text1} = this.text1;
        const {text2} = this.text2;
        const {text3} = this.text3;
        const {x, y} = this.state;
        const maskStyle = {
            '--maskX': x,
            '--maskY': y
        }
        return (
            <div className="titleContainer"
                onMouseMove={this._onMouseMove}
                ref="titleContainer"
                style={maskStyle}
            >
                <div className="titleWrapper">
                    {/* <h1>{text}</h1> */}
                    <h1>{text1}</h1>
                    <h1>{text2}</h1>
                    <h1>{text3}</h1>
                    {/* <img src={Seek2} alt="seek logo layer" id="seekPhoto17" className="seekLogo"></img> */}
                    {/* <img src={Seek1} alt="seek logo layer" id="seekPhoto19" className="seekLogo"></img> */}
                </div>
                <div className="titleWrapper cloneWrapper">
                    {/* <h1>{text}</h1> */}
                    <h1>{text1}</h1>
                    <h1>{text2}</h1>
                    <h1>{text3}</h1>
                    {/* <img src={Seek2} alt="seek logo layer" id="seekPhoto17" className="seekLogo"></img> */}
                    {/* <img src={Seek1} alt="seek logo layer" id="seekPhoto19" className="seekLogo"></img> */}
                </div>
            </div>
        )
    }
}

Title.propTypes = {
    text: PropTypes.string.isRequired
}

export default Title;
