import React, { useState } from "react";
import emailjs from 'emailjs-com';

const EmailForm=() => {
    (function(){
      emailjs.init("user_1zjWdxZg6yFodwsuNkOVU");
  })();

    function sendEmail(e) {
      e.preventDefault();

      emailjs.sendForm('service_41ays4b', 'sendGmailEmail', e.target, 'user_1zjWdxZg6yFodwsuNkOVU')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
        e.target.reset()
    }

    return(
        // <div>
          // <div className="emailContainer">
            <form onSubmit={sendEmail} className="emailContainer">
              <div className="row">
                <div className="form-group emailContainerGroup" id="emailContainer1">
                  <label for="name">Your Name/ Company Name</label>
                  <input type="text" className="form-control" placeholder="Name" name="name"/>
                </div>
                <div className="form-group" id="emailContainer2">
                  <label for="email">Your Email Address</label>
                  <input type="email" className="form-control" placeholder="Email Address" name="email" />
                </div>
              </div>
              {/* <div className="row"> */}
                <div className="col form-group" id="emailContainer3">
                  <label for="subject">Subject</label><br/>
                  <input type="text" className="form-control" placeholder="Subject" name="subject"/>
                </div>
              {/* </div> */}

              <div className="col form-group" id="emailContainer4">
                <label for="message">Message</label><br/>
                <textarea className="form-control" cols="30" rows="8" placeholder="Your message" name="message"></textarea>
              </div>
              <div className="col form-group" id="emailContainer5">
                <input type="submit" className="btn btn-info btn-primary" value="Send Email"></input>
              </div>
            </form>
          // </div>
        // </div>
    )
}

export default EmailForm;


