import { render } from "@testing-library/react";
import React, {Component} from "react";
import Header from "../Header/Header";

class Resource extends Component {
    state={
        active: false,
        visited: false,
    }

    setActive = () => {
        {!this.state.active ?
            this.setState(state => (
                {active: !this.state.active}
            ))
        :
            this.setState(
                {active: false}
            )
        }
    };

    setVisited = () => {
        {!this.state.visited ?
            this.setState(
                {visited: !this.state.visited})
        :
            this.setState(
                {visited: false})
        }
    }

    render(){
        return(
            <div>
                {this.state.active ? 

                <div className="resourceDiv" id={this.props.name+'ParentDiv'}>
                    <iframe 
                    id={this.props.name+'iframe'} 
                    title={this.props.name}
                    src={this.props.iframe}
                    width="80vw"
                    height="60vh"
                    ></iframe>
                    <h6 class="iframeDescription">{this.props.description}</h6>
                    <img id={this.props.name+'Emblem'} className="resourceEmblem" src={this.props.emblem} alt={this.props.emblem+' emblem'}/>
                    <img id={this.props.name+'Logo'} className="resourceLogo" src={this.props.logo} alt={this.props.logo+' logo'}/>
                </div>
                    :
                <div>

                    {/* {this.props.category == } */}
                    <h3>{this.props.name}</h3>
                </div>
                    }
            </div>
        )
    }
}

export default Resource;
