import { render } from "@testing-library/react";
import React, {Component} from "react";
import iframe from 'react-iframe';
import Iframe from 'react-iframe';
import iFrameResize from 'iframe-resizer'


import Header from "../Header/Header";
import FloatDocBox from "./FloatBox/FloatDocBox";

import BachelorsDegree from "./UploadPhotosToWeb/docs/bachelors-undergrad-psychdegree-diploma.pdf"

import MastersDegree from "./UploadPhotosToWeb/docs/MastersDegree.pdf"

import GACertificate from "./UploadPhotosToWeb/docs/GA-Certificate.pdf"

import Resume from "./UploadPhotosToWeb/docs/Liz_Kane_Resume.pdf"


class Resource extends Component {
    state={
        active: false,
        visited: false,
    }

    setActive = () => {
        {!this.state.active ?
            this.setState(state => (
                {active: !this.state.active}
            ))
        :
            this.setState(
                {active: false}
            )
        }
    };

    setVisited = () => {
        {!this.state.visited ?
            this.setState(
                {visited: !this.state.visited})
        :
            this.setState(
                {visited: false})
        }
    }



    render(){
        // iFrameResize = () =>{({ log: true}, '.iframeResize')}
        // <iframe id={this.props.name+'iframe'} title={this.props.name} src={this.props.src} width="560" height="315" frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe>


        return(
            <>
                {this.state.active ? 
                <FloatDocBox name={this.props.name} category={this.props.category} iframe={this.props.iframe} description={this.props.description} emblem={this.props.emblem} logo={this.props.logo}/>
                // <div className="resourceDiv" id={this.props.name+'ParentDiv'} onClose={this.setActive}>
                //     <h2 className="resourceCategory">{this.props.category}</h2>
                //     <h2 className="resourceName">{this.props.name}</h2>
                //     <div className="responsiveIframe">
                //         <iframe id={this.props.name+'iframe'} className="iframeResize" title={this.props.name} src={this.props.iframe} width="560" height="315" frameBorder="0" allowFullScreen></iframe>
                //     </div>
                //     <h6 className="iframeDescription">{this.props.description}</h6>
                //     <img id={this.props.name+'Emblem'} className="resourceEmblem" src={this.props.emblem} alt={this.props.emblem+' emblem'}/>
                //     <img id={this.props.name+'Logo'} className="resourceLogo" src={this.props.logo} alt={this.props.logo+' logo'}/>
                // </div>
                    :
                <div>
                    {/* {this.props.category == } */}
                    <h3 onClick={this.setActive}>{this.props.name}{this.props.category}</h3>
                </div>
                    }
            </>
        )
    }
}

export default Resource;
